    {
        super(props);
        const id = this.props.match.params.id;
        this.state = {
            recipie : {},
            id,
            loading :true
        } 
    }

    async componentDidMount(){
        const url = `https://api.spoonacular.com/recipes/${this.state.id}/information?apiKey=50a92691d65344388c28929af9bf0ac6&includeNutrition=false` ;
        try {
            const response = await fetch(url);
            const responseData = await response.json();
            this.setState ({
                recipie:responseData.recipie,
                loading :false
