        search:'',
        url : `https://api.spoonacular.com/recipes/complexSearch?apiKey=50a92691d65344388c28929af9bf0ac6&query=&number=10`
    };

    async getRecipies(){
        try {
            const data = await fetch(this.state.url);
            const jsondata = await data.json();
            console.log(jsondata);
            this.setState({
                recipies:jsondata
            })
        }
        catch(error){
            console.log(error);
        }
    }

    componentDidMount(){
        this.getRecipies();
    }

    handleChange = (e) =>{
        this.setState({
            search:e.target.value
        });
    };
    handleSubmit = (e) =>{
        e.preventDefault()
    }
    render(){
        return(
            <>
                <Search
                    search = {this.state.search}
                    handleChange = {this.handleChange}
                    handleSubmit = {this.handleSubmit} 
                />
                <RecipieList recipies = {this.state.recipies}/>
            </>
        );
    };
};

export default Recipies;
