import React , {Component} from 'react';
import RecipieList from './RecipieList';
import Search from './Search';
// import {recipieData} from './Export'

class Recipies extends Component
{
    constructor(props){
        super(props);
        this.getRecipies = this.getRecipies.bind(this);
    }
    state = {
        recipies:[],
        loading :true,
        search:'',
        url : `https://api.spoonacular.com/recipes/complexSearch?apiKey=6cc3788ae226433b80206a8eac39a50b&query=`,
        base_url : `https://api.spoonacular.com/recipes/complexSearch?apiKey=6cc3788ae226433b80206a8eac39a50b&query=`,
    };

    async getRecipies(){
        try {
            const data = await fetch(this.state.url);
            const jsondata = await data.json();
            console.log(jsondata);
            this.setState({
                recipies:jsondata.results,
                loading :false
            })
        }
        catch(error){
            console.log(error);
        }
    }

    componentDidMount(){
        this.getRecipies();
    }

    handleChange = (e) =>{
        this.setState({
            search:e.target.value
        });
    };
    handleSubmit = (e) =>{
        e.preventDefault();
        const {base_url, search} = this.state;;
        this.state (
            {
                url:`${base_url}${search}`,
                search : "",
            },
            () => this.getRecipies()
        )
    }
    render(){
        return(
            <>
                <Search
                    search = {this.state.search}
                    handleChange = {this.handleChange}
                    handleSubmit = {this.handleSubmit} 
                />
                <RecipieList recipies = {this.state.recipies}/>
            </>
        );
    };
};

export default Recipies;
